package java_prep;

public class compare2numbersusingifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=20;
		
		if(a>b)
		{
			System.out.println("The largest number is a i.e. " + a);
		}

		else
		{
			System.out.println("The largest number is b i.e. " + b);
		}
		
		
		
		
	}

}
