package java_prep;

public class tofindasciivalueofchar {
//To find ascii value of a character
	//input: a          A-65 ; a-97; 0-48
	//output:97
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char c='0';
		int ascii=c; // 1st way
		System.out.println(ascii);
		
		int asciival=(int)c; //2nd way
		System.out.println(asciival);

	}

}
